INSERT INTO `operation` (`id`, `needs`, `race_id`, `name`, `pic`, `unit_id`, `cost`, `tick`, `description`, `active`, `difficulty`, `max_distance`) VALUES
(1, 100, 0, 'Missile Attack', 'op_rocket.gif', 405, 50, 0, 'Launch an Missile attack against an enemy country. Every rocket has 50% chance in destroying an building.\n\n\"Your enemy will recieve an report about this attack if you succeed and fail.\"', 1, 0.5, 2),
(2, 100, 2, 'Stealth Bombing', 'op_stealthbombing.gif', 404, 15000, 0, 'Launch an Bombing run against an enemy country with your Stealth planes. Every Stealth Bomber is able to hit 5 buildings (train stations, aiports or harbors).\n(Airforce Only)\n\n\"Your enemy will recieve an report about this attack if you succeed and fail. But it will hidde your name if you succeed.\"', 1, 0.5, 0),
(3, 104, 0, 'Spy Technology', 'spy.gif', 407, 150, 0, 'Spy on an enemy country and retrieve important data like buildings and units.\n\n\"If you fail, your enemy recieves an report about your spy attack\"', 1, 0.1, 3),
(4, 100, 1, 'Sniper Team', 'sniper.gif', 402, 250, 0, 'Deploy a Sniper Team behind the enemy lines and take out an ammount of enemy soldiers. Every sniper can kill 5 soldiers, but when you fail, you lose 20% of your snipers!\n(Army Only)\n\n\"Your enemy will recieve an report about this attack if you succeed or fail.\"', 1, 0.5, 0),
(5, 110, 0, 'Nuclear Missile Attack', 'op_nuclear.gif', 408, 2500000, 0, 'Launch an Nuclear Missile Attack against an enemy region.', 1, 0.9, 2),
(6, 100, 3, 'Submarine Attack', 'submarine.gif', 403, 25000, 0, 'Send an Submarine behind enemy lines and sink enemy ships! Every submarine is able to sink at least 1 ship!\n(Navy Only)\n\n\"Your enemy will recieve an report about this attack if you succeed and fail. The report includes your empire name if you fail, else not.\"', 1, 0.5, 0),
(7, 600, 0, 'Destroy Cash', 'destroy_cash.gif', 401, 100, 0, 'Destroy an amount of your enemies cash reserves, based on your \"destroy cash\" research level.', 1, 0.9, 1),
(8, 605, 0, 'Destroy Food', 'destroy_food.gif', 401, 100, 0, 'Destroy an amount of your enemies food reserves, based on your \"destroy food\" research level.', 1, 0.7, 1),
(9, 650, 0, 'Chemical Warfare', 'chemical_warfare.gif', 406, 75, 0, 'Destroy an amount of your enemies population with chemical warheads, based on your \"chemical warfare\" research level.', 1, 0.7, 0),
(10, 105, 0, 'Advanced Spy Operation', 'spy2.gif', 407, 250, 0, 'Spy on an enemy country and retrieve important data like cash and other empire data.\n\n\"If you fail, your enemy recieves an report about your spy attack\"', 1, 0.3, 6),
(11, 105, 0, 'Advanced Spy Operation II', 'spy2.gif', 407, 500, 0, 'Spy on an enemy country and retrieve important data like empire reports.\n\n\"If you fail, your enemy recieves an report about your spy attack\"', 1, 0.5, 9);
